import React, { useEffect, useState } from "react"
import './css/App.css';
import StartStopButton from './components/StartStopButton'
import Timer from 'react-compound-timer'

const App = () => {


  function startWatch() {
    if ("geolocation" in navigator && "getCurrentPosition" in navigator.geolocation) {
      watchPos = navigator.geolocation.getCurrentPosition(setPosition, positionError, {
        enableHighAccuracy: true,
        maximumAge: 0
      });
    } else {
      console.log("Geolocation not available!")
    }

  };
  

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">RDE VeriTest (Dev)</h1>
        <Timer
          initialTime={0}
          startImmediately={false}
          onStart={() => console.log('onStart hook')}
          onStop={() => console.log('onStop hook')}
          onReset={() => console.log('onReset hook')}
        // onResume={() => console.log('onResume hook')}
        // onPause={() => console.log('onPause hook')}
        >
          {({ start, resume, pause, stop, reset, timerState }) => (
            <React.Fragment>
              <div>
                {/* <Timer.Days /> days&nbsp; */}
                <Timer.Hours /> hours&nbsp;
                <Timer.Minutes /> minutes&nbsp;
                <Timer.Seconds /> seconds&nbsp;
                {/* <Timer.Milliseconds /> milliseconds */}
              </div>
              {/* <div>{timerState}</div> */}
              <br />
              <div>
                {/* <button onClick={start}>Start</button>
                <button onClick={pause}>Pause</button>
                <button onClick={resume}>Resume</button>
                <button onClick={stop}>Stop</button> */}
                <StartStopButton startWatch={start} stopWatch={stop} />
                <button onClick={reset}>Reset</button>
              </div>
            </React.Fragment>
          )}
        </Timer>
      </header>
    </div>
  );

};

export default App;
