import { useState } from "react";
import useInterval from "../hooks/useInterval";
// import usePrevious from "../hooks/usePrevious";

const haversine = require('haversine')

function WatchTracker() {
  let id, options;

  const [latitude, setLatitude] = useState(0);
  const [longitude, setLongitude] = useState(0);
  const [accuracy, setAccuracy] = useState(0);
  const [gpsSpeed, setGpsSpeed] = useState(0);
  const [altitude, setAltitude] = useState(0);
  const [timestamp, setTimestamp] = useState(0);


  // const [distance, setDistance] = useState(0);
  const [speed, setSpeed] = useState(0);


  const [coordinates, setCoordinates] = useState([]);

  const [isOn, setIsOn] = useState(false);

  // !!PROBLEM IN DEFINING THESE HERE - not updating in useinterval below!! Can't put inside useInterval, Tried useState but that also doesnt work
  // let oldLat = usePrevious(latitude);
  // let oldLng = usePrevious(longitude);

  let oldLat = 1;
  let oldLng = 1;

  // const [oldLat, setOldLat] = useState(0);
  // const [oldLng, setOldLng] = useState(0);

  function success(pos) {

    // oldLat = pos.coords.latitude;
    // oldLng = pos.coords.longitude;

    // console.log("lat: " + oldLat);
    // console.log("lng: " + oldLng);

    setLatitude(pos.coords.latitude);
    setLongitude(pos.coords.longitude);
    setAccuracy(pos.coords.accuracy);
    setGpsSpeed(pos.coords.speed);
    setAltitude(pos.coords.altitude);
    setTimestamp(pos.timestamp);

    console.log("lat:" + latitude)
    console.log("lng:" + longitude)
  }

  // setOldLat(latitude);
  // setOldLng(longitude);

  useInterval(() => {
    setCoordinates(
      coordinates.concat(...[
        latitude,
        longitude,
        altitude,
        speed,
        gpsSpeed,
        timestamp,
        accuracy
      ])
    )

    // console.log(JSON.stringify(coordinates));
    // localStorage.setItem("coordinates", JSON.stringify(coordinates));

    // To start logging
    // if (latitude === 0 && longitude === 0) {
    //   return
    // }

    // let start = {
    //   latitude: latitude,
    //   longitude: longitude,
    // }

    // let end = {
    //   latitude: oldLat,
    //   longitude: oldLng,
    // }

    // let havDis = haversine(start, end, { unit: 'km' })

    // setDistance(prev => prev + havDis);
    // console.log(distance);

  }, isOn ? 1000 : null

  )

  function error(err) {
    console.warn('ERROR(' + err.code + '): ' + err.message);
  }

  options = {
    enableHighAccuracy: true,
    maximumAge: 0
  };

  function startWatch() {
    setIsOn(true);
    console.log("Starting Watch.")
    if ("geolocation" in navigator && "watchPosition" in navigator.geolocation) {
      id = navigator.geolocation.watchPosition(success, error, options);
    } else {
      console.log("Geolocation not available.")
    }
  }

  function stopWatch() {
    setIsOn(false);
    console.log("Stopping Watch.");
    navigator.geolocation.clearWatch(id);
  }

  return (
    <div>
      <div>[TESTS E, F]</div>
      <button onClick={startWatch}>start</button>
      <button onClick={stopWatch}>stop</button>
    </div>
  )

}

export default WatchTracker
