import React, { useState } from "react"
import Sound from "react-sound";
import start from "../sounds/start.mp3"
import stop from "../sounds/stop.mp3"

const StartStopButton = (props) => {

    let [startDetect, setStartDetect] = useState(false);
    const [text, setText] = useState("Start Tracking");
    const [startPlaying, setStartPlaying] = useState(false);

    const startButtonStyles = {
        background: "green",
        borderRadius: "5px",
        fontSize: "2rem",
        color: "white",
        padding: "6px 6px 6px"
    }

    const stopButtonStyles = {
        background: "red",
        borderRadius: "5px",
        fontSize: "2rem",
        color: "white",
        padding: "6px 6px 6px"
    }

    let [buttonStyles, setButtonStyles] = useState(startButtonStyles);

    const StartHandler = () => {
        setStartDetect(startDetect = !startDetect);
        setText(startDetect ? "Stop Tracking" : "Start Tracking")
        setButtonStyles(startDetect ? stopButtonStyles : startButtonStyles)
        if (startDetect === true) {
            props.startWatch();
            <Sound 
                playStatus={Sound.status.PLAYING}
                onFinishedPlaying={setStartPlaying(false)}
            />
        } else if (startDetect === false) {
            props.stopWatch();
        }
    }

    return (
        <div>
            <button style={buttonStyles} onClick={StartHandler}>{text}</button>
        </div>
    )
}

export default StartStopButton
