import React, { useState } from "react"
import './App.css';
import StartStopButton from './StartStopButton'

const App = () => {

  let watchPos;

  function startWatch() {
    if ("geolocation" in navigator && "getCurrentPosition" in navigator.geolocation) {
      watchPos = navigator.geolocation.getCurrentPosition(setPosition, positionError, {
        enableHighAccuracy: true,
        maximumAge: 0
      });
    } else {
      console.log("Geolocation not available!")
    }

  };

  let coordinates = [];

  // need to make these useState
  let speed = 0;
  let altitude = 0;
  let distance = 0;
  let [latitude, setLatitude] = useState(0);
  let longitude = 0;

  function setPosition(position) {
    setLatitude = position.coords.latitude;
    let longitude = position.coords.longitude;
    let timestamp = position.coords.timestamp;
    let altitude = position.coords.altitude;
    let speed = position.coords.speed;

    coordinates.push([latitude, longitude, timestamp, altitude, speed]);

    console.log(window.localStorage.setItem("coordinates", JSON.stringify(coordinates)));
  }

  function stopWatch() {
    navigator.geolocation.clearWatch(watchPos);
    watchPos = undefined;
    console.log("Tracking Stopped.")
    console.log(coordinates);
  }

  function positionError(error) {
    switch (error.code) {
      case error.PERMISSION_DENIED:
        console.error("User denied Geolocation request.");
        break;
      case error.POSITION_UNAVAILABLE:
        console.error("Position unavailable.");
        break;
      case error.TIMEOUT:
        console.error("Request timed out.");
        break;
      case error.UNKNOWN_ERROR:
        console.error("An unknown error occured.");
        break;
      default:
        console.warn('ERROR(' + error.code + '): ' + error.message);
        break;
    }
  };


  return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">RDE VeriTest (Dev)</h1>
        <div>
          <StartStopButton startWatch={startWatch} stopWatch={stopWatch} />
        </div>
        <div>latitude is: {latitude}</div>
        <div>speed is:</div>
        <div>time is:</div>
        <div>cumulative distance is: </div>
        <div>altitude is: </div>
      </header>
    </div>
  );

};

export default App;
