import React, { useEffect, useState } from "react"
import './App.css';
import StartStopButton from './StartStopButton'

const App = () => {

  let watchPos;
  // let [stopWatch2, setStopWatch2] = useState(false);


  function startWatch() {
    if ("geolocation" in navigator && "watchPosition" in navigator.geolocation) {
      watchPos = navigator.geolocation.watchPosition(setPosition, positionError, {
        enableHighAccuracy: true,
        maximumAge: 0
      });
    } else {
      console.log("Geolocation not available!")
    }

  };

  // setStopWatch2(stopWatch2 = true);

  let coordinates = [];

  let [speed, setSpeed] = useState(0);
  let [altitude, setAltitude] = useState(0);
  let [distance, setDistance] = useState(0);
  let [latitude, setLatitude] = useState(0);
  let [longitude, setLongitude] = useState(0);


  // TIMER

  let [time, setTime] = useState(0);
  let [timerOn, setTimerOn] = useState(false);

  useEffect(() => {
    let interval = null;

    if (timerOn) {
      interval = setInterval(() => {
        setTime(prevTime => prevTime + 10)
      }, 10)
    } else {
      clearInterval(interval)
    }

    return () => clearInterval(interval)
  }, [timerOn])


  // WATCH POSITION SETTER 

  function setPosition(pos) {

    setLatitude = latitude = pos.coords.latitude;
    setLongitude = longitude = pos.coords.longitude;
    setAltitude = altitude = pos.coords.altitude;
    setSpeed = speed = pos.coords.speed;

    // set time goes here

    coordinates.push([latitude, longitude, altitude, speed]);
    console.log(coordinates);
    console.log(localStorage.setItem("coordinates", JSON.stringify(coordinates)));
  }

  function stopWatch() {
    navigator.geolocation.clearWatch(watchPos);
    watchPos = undefined;
    console.log("Tracking Stopped.")
    console.log(coordinates);
  }

  function positionError(error) {
    switch (error.code) {
      case error.PERMISSION_DENIED:
        console.error("User denied Geolocation request.");
        break;
      case error.POSITION_UNAVAILABLE:
        console.error("Position unavailable.");
        break;
      case error.TIMEOUT:
        console.error("Request timed out.");
        break;
      case error.UNKNOWN_ERROR:
        console.error("An unknown error occured.");
        break;
      default:
        console.warn('ERROR(' + error.code + '): ' + error.message);
        break;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">RDE VeriTest (Dev)</h1>
        <div>
          <StartStopButton startWatch={startWatch} stopWatch={stopWatch} />
        </div>
        <div className="Data-Label">speed is: {speed} km/h</div>
        <div className="Data-Label">time is: mm </div>
        <div className="Data-Label">cumulative distance is: </div>
        <div className="Data-Label">altitude is: {altitude}</div>
        <div className="Data-Label">latitude is: {latitude}</div>
        <div className="Data-Label">longitude is: {longitude}</div>
      </header>
    </div>
  );

};

export default App;
