import React, { useState } from "react"

const StartStopButton = (props) => {

    const startStopDetector = false;
    
    let text = "Start";

    const StartHandler = () => {
        startStopDetector = !startStopDetector;
        console.log(startStopDetector);
        text = startStopDetector ? "Stop" : "Start";
        console.log(text);
    }

    const buttonStyles = {
        background: props.buttonColor,
        borderRadius: "5px"
    }

    return (
        <div>
            <button style={buttonStyles} onClick={StartHandler}>{text}</button>
        </div>
    )
}

export default StartStopButton
