import React, { useState, useEffect } from "react"
// import logo from './logo.svg';
import './App.css';
import StartStopButton from './StartStopButton'

// import { render } from "@testing-library/react";

const App = () => {

  let watchPos;

  function startWatch() {
    if (!watchPos) {
      if ("geolocation" in navigator && "watchPosition" in navigator.geolocation) {
        watchPos = navigator.geolocation.watchPosition(setCurrentPosition, positionError, {
          enableHighAccuracy: true,
          timeout: 15000,
          maximumAge: 0
        });
      }
    }
  };

  function setCurrentPosition(position) {

    document.querySelector('.accuracy').innerHTML = position.coords.accuracy;
    document.querySelector('.altitude').innerHTML = position.coords.altitude;
    document.querySelector('.altitudeAccuracy').innerHTML = position.coords.altitudeAccuracy;
    document.querySelector('.heading').innerHTML = position.coords.heading;
    document.querySelector('.latitude').innerHTML = position.coords.latitude;
    document.querySelector('.longitude').innerHTML = position.coords.longitude;
    document.querySelector('.speed').innerHTML = position.coords.speed;
  }

  function stopWatch() {
    navigator.geolocation.clearWatch(watchPos);
    watchPos = undefined;
  }

  function positionError(error) {
    switch (error.code) {
      case error.PERMISSION_DENIED:
        console.error("User denied Geolocation request.");
        break;
      case error.POSITION_UNAVAILABLE:
        console.error("Position unavailable.");
        break;
      case error.TIMEOUT:
        console.error("Request timed out.");
        break;
      case error.UNKNOWN_ERROR:
        console.error("An unknown error occured.");
        break;
      default:
        console.warn('ERROR(' + error.code + '): ' + error.message);
        break;
    }
  };

  let buttonText = "Start/Stop";

  return (
    <div className="App">
      <header className="App-header">
        {/* <img src={logo} className="App-logo" alt="logo" /> */}
        <h1 className="App-title">RDE VeriTest (Dev)</h1>
        <div>
          <StartStopButton greeting={"h"} text={buttonText} />
          console.console.log();
        </div>
      </header>
    </div>
  );

};

export default App;
