import React, { useState } from "react"

const StartStopButton = (props) => {

    const [startStop, setStartStop] = useState(false);


    let setStar

    let text = "Start";
    const StartHandler = () => {
        // startStopDetector = !startStopDetector
        text = startStop ? "Stop" : "Start";
    }

    const buttonStyles = {
        background: props.buttonColor,
        borderRadius: "5px"
    }

    return (
        <div>
            <button style={buttonStyles} onClick={StartHandler}>{text}</button>
        </div>
    )
}

export default StartStopButton
