import React, { useState } from "react"

const StartStopButton = (props) => {

    let [startDetect, setStartDetect] = useState(false);
    let [text, setText] = useState("Start Tracking");

    const startButtonStyles = {
        // background: "green",
        // borderRadius: "5px",
        // fontSize: "2rem",
        // color: "white",
        // padding: "10px 10px 10px"
    }

    const stopButtonStyles = {
        // background: "red",
        // borderRadius: "5px",
        // fontSize: "2rem",
        // color: "white",
        // padding: "10px 10px 10px"
    }

    let [buttonStyles, setButtonStyles] = useState(startButtonStyles);

    const StartHandler = () => {
        setStartDetect(startDetect = !startDetect);
        setText(startDetect ? "Stop Tracking" : "Start Tracking")
        setButtonStyles(startDetect ? stopButtonStyles : startButtonStyles)
        if (startDetect === true) {
            props.startWatch()
            props
        } else if (startDetect === false) {
            props.stopWatch()
        }
    }

    return (
        <div>
            <button style={buttonStyles} onClick={StartHandler}>{text}</button>
        </div>
    )
}

export default StartStopButton
