import React, { useState } from "react";
import useInterval from "../hooks/useInterval";
import StartStopButton from "./StartStopButton";
import Timer from 'react-compound-timer'

const haversine = require('haversine')

function WatchTracker() {
  let id, options;

  let urbanMax = 33;
  let ruralMax = 55;
  let motorwayMax = 80;

  const [latitude, setLatitude] = useState(0);
  const [longitude, setLongitude] = useState(0);
  const [accuracy, setAccuracy] = useState(0);
  const [gpsSpeed, setGpsSpeed] = useState(0);
  const [altitude, setAltitude] = useState(0);
  const [timestamp, setTimestamp] = useState(0);


  const [distance, setDistance] = useState(0);
  const [speed, setSpeed] = useState(0);

  const [urbanDist, setUrbanDist] = useState(0);
  const [ruralDist, setRuralDist] = useState(0);
  const [motorwayDist, setMotorwayDist] = useState(0);

  const [coordinates, setCoordinates] = useState([0, 0, 0, 0, 0, 0, 0]);

  const [index, setIndex] = useState(0);

  const [isOn, setIsOn] = useState(false);

  // !!PROBLEM IN DEFINING THESE HERE - not updating in useinterval below!! Can't put inside useInterval, Tried useState but that also doesnt work
  // let oldLat = usePrevious(latitude);
  // let oldLng = usePrevious(longitude);

  // let oldLat = 0;
  // let oldLng = 0;

  // const [oldLat, setOldLat] = useState(0);
  // const [oldLng, setOldLng] = useState(0);

  function success(pos) {

    // oldLat = pos.coords.latitude;
    // oldLng = pos.coords.longitude;

    // start = {
    //   latitude: oldLat,
    //   longitude: oldLng,
    // }

    // start.latitude = oldLat;
    // start.longitude = oldLng;

    setLatitude(pos.coords.latitude);
    setLongitude(pos.coords.longitude);
    setAccuracy(pos.coords.accuracy);
    setGpsSpeed(Math.round(pos.coords.speed * 100) / 100);
    setAltitude(pos.coords.altitude);
    setTimestamp(pos.timestamp);

  }

  // setOldLat(latitude);
  // setOldLng(longitude);

  useInterval(() => {
    setGpsSpeed((gpsSpeed === null) ? 0 : gpsSpeed); // set to 0 to mask null value
    setCoordinates(
      coordinates.concat(...[
        latitude,
        longitude,
        altitude,
        speed,
        gpsSpeed,
        timestamp,
        accuracy
      ])
    )

    // console.log(JSON.stringify(coordinates));
    // localStorage.setItem("coordinates", JSON.stringify(coordinates));

    // log lats and longs (new):
    console.log("lat:" + latitude)
    console.log("lng:" + longitude)

    let end = {
      latitude: latitude,
      longitude: longitude,
    }

    let oldLat = coordinates[index];
    let oldLng = coordinates[index + 1];

    setIndex(i => i + 7)

    let start = {
      latitude: oldLat,
      longitude: oldLng,
    }

    // log lats and longs (old):
    console.log("oldLat: " + start.latitude)
    console.log("oldLng: " + start.longitude)

    let havDis = haversine(start, end, { unit: 'km' })

    if (start.latitude === 0 && start.longitude === 0) {
      return
    }

    setDistance(prev => prev + havDis);

    // log cumulative distance:
    console.log(distance);

    let startTime = coordinates[index + 5];
    let endTime = timestamp;
    let deltaT = (endTime - startTime) / 3.6e+6; // ms -> hr;
    deltaT = (deltaT === 0) ? 1 : deltaT; // set to 1 to mask NaN error
    let roundedSpd = Math.round((havDis / deltaT) * 10) / 10 // round to 1dp
    setSpeed(roundedSpd);

    if (speed < urbanMax) {
      setUrbanDist(prev => prev + Math.round(havDis))
    } else if (speed < ruralMax) {
      setRuralDist(prev => prev + Math.round(havDis))
    } else if (speed < motorwayMax) {
      setMotorwayDist(prev => prev + Math.round(havDis))
    } else if (speed > motorwayMax) {
      console.log("Max Speed Exceeded!")
    }

    let urbanShare = (urbanDist / distance);
    let ruralShare = (ruralDist / distance);
    let motorwayShare = (motorwayDist / distance);

  }, isOn ? 1000 : null
  )

  function error(err) {
    console.warn('ERROR(' + err.code + '): ' + err.message);
  }

  options = {
    enableHighAccuracy: true,
    maximumAge: 0
  };

  function startWatch() {
    console.log("Starting Watch.")
    if ("geolocation" in navigator && "watchPosition" in navigator.geolocation) {
      id = navigator.geolocation.watchPosition(success, error, options);
    } else {
      console.log("Geolocation not available.")
    }
  }

  function stopWatch() {
    console.log("Stopping Watch.");
    navigator.geolocation.clearWatch(id);
    setDistance(0);
  }

  return (
    <React.Fragment>
      <div>[TESTS E, F]</div>
      <Timer
        initialTime={0}
        startImmediately={false}
        onStart={() => {
          // console.log('onStart hook');
          startWatch();
          setIsOn(true);
        }}
        onStop={() => {
          // console.log('onStop hook');
          stopWatch();
          setIsOn(false);
        }}
        onReset={() => {
          // console.log('onReset hook')
          setIsOn(false);
          stopWatch();
          setCoordinates([0, 0, 0, 0, 0, 0, 0]);
          setIndex(0);
          setUrbanDist(0);
          setRuralDist(0);
          setMotorwayDist(0);
          console.log("Reset.")
          // localStorage.setItem("coordinates", JSON.stringify(coordinates));
        }}
      >
        {({ start, resume, pause, stop, reset, timerState }) => (
          <React.Fragment>
            <Timer.Hours /> Hrs&nbsp;
            <Timer.Minutes /> Min&nbsp;
            <Timer.Seconds /> Sec&nbsp;
            <StartStopButton startWatch={start} stopWatch={stop} resetWatch={reset} />
            <button onClick={reset}>Reset</button>
          </React.Fragment>
        )}
      </Timer>
      <div>Speed (GPS, Derived) [km/h]:</div>
      <div className="Data-Label">{gpsSpeed}, {speed}</div>
      <div>Distance Shares (U,R,M) [km]:</div>
      <div className="Data-Label">{urbanDist}, {ruralDist}, {motorwayDist}</div>
      <div>Trip segment (U,R,M):</div>
      <div className="Data-Label">
        {speed === 0 &&
          "Pre-Test"
        }
        {speed < urbanMax && speed === !0 &&
          "U"
        }
        {speed > urbanMax && speed < ruralMax &&
          "R"
        }
        {speed > ruralMax && speed < motorwayMax &&
          "M"
        }
      </div>
      <div className="Data-Label">{}, {}, {}</div>
    </React.Fragment>
  )

}

export default WatchTracker
