import React, { useState } from "react"

const StartStopButton = (props) => {

    let [startDetect, setStartDetect] = useState(false);
    let [text, setText] = useState("Start");
    
    let [buttonStyles, setButtonStyles] = useState({
        background: "white",
        borderRadius: "5px",
        fontSize: "2rem"
    })

    const StartHandler = () => {
        setStartDetect(startDetect = !startDetect);
        setText(startDetect ? "Stop" : "Start")
        setButtonStyles(background)
    }


    return (
        <div>
            <button style={buttonStyles} onClick={StartHandler}>{text}</button>
        </div>
    )
}

export default StartStopButton
