import React, { useState, useEffect } from "react"
import Timer from 'react-compound-timer'
import StartStopButton from './StartStopButton'
import useInterval from '../hooks/useInterval'

const Tracker = () => {

    const delay = 1000;
    const [isOn, setIsOn] = useState(false)

    let coordinates = [];

    let [speed, setSpeed] = useState(0);
    let [altitude, setAltitude] = useState(0);
    let [latitude, setLatitude] = useState(0);
    let [longitude, setLongitude] = useState(0);
    // let [distance, setDistance] = useState(0);

    useEffect(() => {
        console.log(`Latitude Updated: ${latitude}`)
    }, [latitude])

    useEffect(() => {
        console.log(`Longitude Updated: ${longitude}`)
    }, [longitude])

    useInterval(() => {
        if ("geolocation" in navigator && "getCurrentPosition" in navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (pos) => {
                    setLatitude(pos.coords.latitude);
                    setLongitude(pos.coords.longitude);
                    setAltitude(pos.coords.altitude);
                    setSpeed(pos.coords.speed);

                    coordinates.push([latitude, longitude, altitude, speed]);
                    console.log(coordinates);
                    console.log(localStorage.setItem("coordinates", JSON.stringify(coordinates)));
                },
                () => { console.log('couldnt be bothered to correctly capture the error so... here you are') }
                , {
                    enableHighAccuracy: true,
                    maximumAge: 0
                });
        } else {
            console.log("Geolocation not available!")
        }
    },
        isOn ? delay : null,
    )

    return (
        <Timer
            initialTime={0}
            startImmediately={false}
            onStart={() => {
                console.log('onStart hook');
                setIsOn(true);
            }}
            onStop={() => {
                console.log('onStop hook');
                setIsOn(false);
            }}
            onReset={() => {
                console.log('onReset hook')
                setIsOn(false);
                // TODO: {
                // Clear array of variables and reset all useStates to 0
                // Clear local storage
                // } 
            }}
        >
            {({ start, resume, pause, stop, reset, timerState }) => (
                <React.Fragment>
                    <div>
                        <Timer.Hours /> Hrs&nbsp;
                        <Timer.Minutes /> Min&nbsp;
                        <Timer.Seconds /> Sec&nbsp;
                    </div>
                    <div>
                        <StartStopButton startWatch={start} stopWatch={stop} />
                        <button onClick={reset}>Reset</button>
                    </div>
                    <div> BOUNDARY CONDITIONS </div>
                    <div>speed is: {speed} [km/h]</div>
                    <div>trip segment is: ____ [U/R/M]</div>
                    <div>altitude is: {altitude} [m]</div>
                    <div>cumulative distance is: ___ [km]</div>
                    <div>latitude is: {latitude} [&deg;]</div>
                    <div>longitude is: {longitude} [&deg;]</div>






                </React.Fragment>
            )}
        </Timer >
    )
}

export default Tracker
